void
gst_bit_reader_init (GstBitReader *reader,
                     const guint8 *data,
                     guint size);

typedef struct {
  const guint8 *data;
  guint size;

  guint byte;  /* Byte position */
  guint bit;   /* Bit position in the current byte */
} GstBitReader;
